import React, { useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const SearchPage = () => {
  const [query, setQuery] = useState("");
  const [results, setResults] = useState([]);

  const handleSearch = (e) => {
    e.preventDefault();
    axios
      .get(`http://localhost:4000/api/drinks?search=${query}`)
      .then((response) => setResults(response.data.items))
      .catch((error) => console.error("Error searching drinks:", error));
  };

  return (
    <div>
      <h2 className="text-2xl mb-4">Search Root Beers</h2>
      <form onSubmit={handleSearch} className="mb-4">
        <input
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          className="w-full p-2 border rounded"
          placeholder="Search root beers..."
        />
        <button type="submit" className="px-4 py-2 bg-blue-500 text-white rounded mt-2">
          Search
        </button>
      </form>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        {results.map((drink) => (
          <Link to={`/drink/${drink.id}`} key={drink.id}>
            <div className="p-4 border rounded shadow-sm">
              <h3 className="text-xl font-bold">{drink.name}</h3>
              <p>{drink.description}</p>
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
};

export default SearchPage;
