import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const Dashboard = () => {
  const [drinks, setDrinks] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:4000/api/drinks?offset=0&length=10")
      .then((response) => setDrinks(response.data.items))
      .catch((error) => console.error("Error fetching drinks:", error));
  }, []);

  return (
    <div>
      <h2 className="text-2xl mb-4">Recently Added Root Beers</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        {drinks.map((drink) => (
          <Link to={`/drink/${drink.id}`} key={drink.id}>
            <div className="p-4 border rounded shadow-sm">
              <h3 className="text-xl font-bold">{drink.name}</h3>
              <p>{drink.description}</p>
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
};

export default Dashboard;
